import React, { useEffect, useState, useRef } from 'react';
import About from '../About/about'
import Education from '../Education/education'
import Experience from '../Experience/experience';
import Publication from '../Publication/publication'
import {
    NavBarContainer,
    NavigationContainer,
    NavigationButton
} from './navBar.styled'


const NavBar = () => {
    // let aboutRef = useRef()
    // let exRef = useRef()
    // let pubRef = useRef()
    // let eduRef = useRef()
    const [navLinkId, setNavLinkId] = useState('')
    useEffect(() => {
        setTimeout(() => console.log(document.getElementById(navLinkId)), 500)
    }, [navLinkId])

    return (
        <>
        <NavBarContainer>
            <NavigationContainer>
                <NavigationButton onClick={() => setNavLinkId('about')}>About</NavigationButton>
                <NavigationButton onClick={() => setNavLinkId('experience')}>Experience</NavigationButton>
            </NavigationContainer>
        </NavBarContainer>

        <About id="about"  />
        <Experience id="experience" />
        <Publication id="publication" />
        <Education id="education" />
        </>

    )
}

export default NavBar;
