import React, { useEffect, useState } from 'react';
import { HashLink as Link } from 'react-router-hash-link';
import {
    NavBarContainer,
    NavigationContainer,
    NavigationButton
} from './navBar.styled'


const NavBar = ({ handleRefObj }) => {
    const [navLinkId, setRef] = useState('')
    // useEffect(() => {
    //     console.log(handleRefObj[navRef])
    //     handleRefObj[navRef]()
    //     // if(!handleRefObj[navRef]){
    //     //     handleRefObj[navRef]()
    //     // }
    // }, [navLinkId])
    const handleClick = (scrollToId) => {
        console.log(document.getElementById(scrollToId))
		document.getElementById(scrollToId).scrollIntoView({
			behavior: 'smooth', // gives an ease-in-out effect to our scroll
		});
	};

    return (
        <NavBarContainer>
            <NavigationContainer>
                <NavigationButton onClick={() => handleClick('about')}>About</NavigationButton>
                <NavigationButton onClick={() => handleClick('experience')}>Experience</NavigationButton>
            </NavigationContainer>
        </NavBarContainer>
    )
}

export default NavBar;
