import './App.css';
import React, { useRef, useEffect, useState} from 'react';
import About from './About/about'
import Education from './Education/education'
import Experience from './Experience/experience';
import Publication from './Publication/publication'
import {
  NavBarContainer,
  NavigationContainer,
  NavigationButton
} from './NavBar/navBar.styled'

function App() {
  const [idName, setIdName ] = useState('')
  function scrollTo(e, id) {
    const el = document.getElementById(id)
    e.preventDefault();
    el && el.scrollIntoView({ behavior: "smooth", block: "start" });
    console.log(el)
    setIdName('about')
    // if (!ref.current) return;
    // ref.current.scrollIntoView({ behavior: "smooth" });
  }
  const ref = useRef()
  const aboutRef = useRef()
  const exRef = useRef()
  const pubRef = useRef()
  const eduRef = useRef()

  useEffect(() => {
    let element = document.getElementById('experience');
    console.log(element)
    element && element.scrollIntoView({ behavior: "smooth", block: "start" });
  }, [idName]);

  return (
    <div className="App" ref={ref}>
        <About id="about" ref={aboutRef} />
        <Experience id="experience" ref={exRef}/>
        <Publication id="publication" ref={pubRef}/>
        <Education id="education" ref={eduRef}/>
        <NavBarContainer>
            <NavigationContainer>
                <NavigationButton onClick={(e) => {scrollTo(e, 'about') }}>About</NavigationButton>
                <NavigationButton onClick={(e) => scrollTo(e, 'experience')}>Experience</NavigationButton>
            </NavigationContainer>
        </NavBarContainer>

    </div>
  );
}

export default App;
