import './App.css';
import React, { useEffect, useState} from 'react';
import About from './About/about'
import Education from './Education/education'
import Experience from './Experience/experience';
import Publication from './Publication/publication'
import {
  NavBarContainer,
  NavigationContainer,
  NavigationButton
} from './NavBar/navBar.styled'

function App() {
  const [idName, setIdName ] = useState('')
  function scrollTo(e, id) {
    const el = document.getElementById(id)
    e.preventDefault();
    el && el.scrollIntoView({ behavior: "smooth", block: "start" });
    console.log(el, 'el')
    setTimeout(() => setIdName('about'), 500)
    // if (!ref.current) return;
    // ref.current.scrollIntoView({ behavior: "smooth" });
  }


  useEffect(() => {
    let element = document.getElementById('experience');
    console.log(document)
    if(element){
      console.log(element, 'element')
      element.scrollIntoView({ behavior: "smooth", block: "start" });
    }
  }, [idName]);

  return (
    <div className="App">
        <About />
        <Experience />
        <Publication />
        <Education />
        <NavBarContainer>
            <NavigationContainer>
                <NavigationButton onClick={(e) => setIdName('about')}>About</NavigationButton>
                <NavigationButton onClick={(e) => setIdName('experience')}>Experience</NavigationButton>
            </NavigationContainer>
        </NavBarContainer>

    </div>
  );
}

export default App;
