import './App.css';
import React, { useRef} from 'react';
import NavBar from './NavBar/navBar'
import About from './About/about'
import Education from './Education/education'
import Experience from './Experience/experience';
import Publication from './Publication/publication'
import {
  BrowserRouter,
  Route,
  Routes,
  HashRouter
} from "react-router-dom";

function App() {
  const aboutRef = useRef(null)
  const exRef = useRef(null)
  const pubRef = useRef(null)
  const eduRef = useRef(null)
  const handleRefObj = 
    { about: () => {
      if(aboutRef){
        aboutRef.current.scrollIntoView({ behavior: 'smooth' })
      }    
    },
    education: () => {
      if(eduRef){
        eduRef.current.scrollIntoView({ behavior: 'smooth' })
      }    
    },
    experience: () => {
      if(exRef){
        exRef.current.scrollIntoView({ behavior: 'smooth' })
      }    
    },
    publication: () => {
      if(pubRef){
        pubRef.current.scrollIntoView({ behavior: 'smooth' })
      }    
    },
  }
  return (
    <div className="App">
          <NavBar handleRefObj={handleRefObj}/>
          <About id="about" ref={aboutRef} />
          <Experience id="experience" ref={exRef}/>
          <Publication id="publication" ref={pubRef}/>
          <Education id="education" ref={eduRef}/>

    </div>
  );
}

export default App;
