    border: solid 10px white;
    z-index: 1;
    position: relative;
    top: -150px; 
`;

export const PhotoBackground = styled.div`
    width: 100%;
    background: grey;
    opacity: .7;
    height: 300px;
`
export const AboutContent = styled.div`
    top: -120px; 
    position: relative;
`;
export const AboutText = styled.p`
    color: #2B303A;
