import './App.css';
import React, { useRef} from 'react';
import About from './About/about'
import Education from './Education/education'
import Experience from './Experience/experience';
import Publication from './Publication/publication'
import {
  NavBarContainer,
  NavigationContainer,
  NavigationButton
} from './NavBar/navBar.styled'

function App() {
  function scrollTo(ref, id) {
    document.getElementById(id)
    if (!ref.current) return;
    ref.current.scrollIntoView({ behavior: "smooth" });
  }
  const ref = useRef()
  const aboutRef = useRef()
  const exRef = useRef()
  const pubRef = useRef()
  const eduRef = useRef()

  return (
    <div className="App" ref={ref}>
        <About id="about" ref={aboutRef} />
        <Experience id="experience" ref={exRef}/>
        <Publication id="publication" ref={pubRef}/>
        <Education id="education" ref={eduRef}/>
        <NavBarContainer>
            <NavigationContainer>
                <NavigationButton onClick={() => scrollTo(aboutRef, 'about')}>About</NavigationButton>
                <NavigationButton onClick={() => scrollTo(exRef)}>Experience</NavigationButton>
            </NavigationContainer>
        </NavBarContainer>

    </div>
  );
}

export default App;
