import './App.css';
import React, { useRef} from 'react';
import NavBar from './NavBar/navBar'
import About from './About/about'
import Education from './Education/education'
import Experience from './Experience/experience';
import Publication from './Publication/publication'
import {
  BrowserRouter,
  Route,
  Routes,
  HashRouter
} from "react-router-dom";
import {
  NavBarContainer,
  NavigationContainer,
  NavigationButton
} from './navBar/navBar.styled'

function App() {
  function scrollTo(ref) {
    if (!ref.current) return;
    ref.current.scrollIntoView({ behavior: "smooth" });
  }
  const aboutRef = useRef()
  const exRef = useRef()
  const pubRef = useRef()
  const eduRef = useRef()
  const handleRefObj = 
    { about: aboutRef,
    education: eduRef,
    experience: exRef,
    publication: pubRef,
  }
  return (
    <div className="App" ref={aboutRef}>
      <NavBarContainer>
            <NavigationContainer>
                <NavigationButton onClick={() => scrollTo(aboutRef)}>About</NavigationButton>
                <NavigationButton onClick={() => scrollTo(exRef)}>Experience</NavigationButton>
            </NavigationContainer>
        </NavBarContainer>
          <About id="about" ref={aboutRef} />
          <Experience id="experience" ref={exRef}/>
          <Publication id="publication" ref={pubRef}/>
          <Education id="education" ref={eduRef}/>

    </div>
  );
}

export default App;
