import './App.css';
import React, { useRef, useEffect, useState} from 'react';
import NavBar from './NavBar/navBar'
import About from './About/about'
import Education from './Education/education'
import Experience from './Experience/experience';
import Publication from './Publication/publication'
import {
  BrowserRouter,
  Route,
  Routes,
  HashRouter
} from "react-router-dom";
import {
  NavBarContainer,
  NavigationContainer,
  NavigationButton
} from './NavBar/navBar.styled'

function App() {
  const aboutRef = useRef()
  const exRef = useRef()
  const pubRef = useRef()
  const eduRef = useRef()
  const handleRefObj = 
    { about: aboutRef,
    education: eduRef,
    experience: exRef,
    publication: pubRef,
  }
  const [currentRef, setNavLinkId] = useState(null)
  useEffect(() => {
      if(currentRef && currentRef.current){
          currentRef.current.scrollIntoView({behavior: 'smooth'})
      }
  }, [currentRef])
  return (
    <div className="App">
          <About id="about" ref={aboutRef} />
          <Experience id="experience" ref={exRef} />
          <Publication id="publication" ref={pubRef}/>
          <Education id="education" ref={eduRef}/>
          <NavBarContainer>
            <NavigationContainer>
                <NavigationButton onClick={() => setNavLinkId(aboutRef)}>About</NavigationButton>
                <NavigationButton onClick={() => setNavLinkId(exRef)}>Experience</NavigationButton>
            </NavigationContainer>
        </NavBarContainer>

    </div>
  );
}

export default App;
