import React, { useEffect , useState} from 'react';
import {
    NavBarContainer,
    NavigationContainer,
    NavigationButton
} from './navBar.styled'


const NavBar = () => {
    const [anchor, setAnchor ] = useState('')
    useEffect(() => {
        const anchorTag = setTimeout(() => document.getElementById(anchor), 500)
        anchorTag.scrollIntoView({ behavior: 'smooth' })
    }, [anchor])

    return (
        <NavBarContainer>
            <NavigationContainer>
                <NavigationButton onClick={() => setAnchor('about')}>About</NavigationButton>
                <NavigationButton onClick={() => setAnchor('experience')}>Experience</NavigationButton>
                <NavigationButton onClick={() => setAnchor('publication')}>Publication</NavigationButton>
                <NavigationButton onClick={() => setAnchor('education')}>Education</NavigationButton>
            </NavigationContainer>
        </NavBarContainer>
    )
}

export default NavBar;
