import React, { useEffect, useState, useRef } from 'react';
import About from '../About/about'
import Education from '../Education/education'
import Experience from '../Experience/experience';
import Publication from '../Publication/publication'
import {
    NavBarContainer,
    NavigationContainer,
    NavigationButton
} from './navBar.styled'


const NavBar = () => {
    const [navId, setIdName] = useState('')

    useEffect(() => {
        let element = document.getElementById(navId);
        console.log(document)
        if(element){
          console.log(element, 'element')
          element.scrollIntoView({ behavior: "smooth", block: "start" });
        }
      }, [navId]);

    return (
        <>
        <NavBarContainer>
            <NavigationContainer>
                <NavigationButton onClick={(e) => setIdName('about')}>About</NavigationButton>
                <NavigationButton onClick={(e) => setIdName('experience')}>Experience</NavigationButton>
                <NavigationButton onClick={(e) => setIdName('experience')}>Publication</NavigationButton>
                <NavigationButton onClick={(e) => setIdName('experience')}>Education</NavigationButton>
                </NavigationContainer>
        </NavBarContainer>

        <About id="about" />
        <Experience id="experience" />
        <Publication id="publication" />
        <Education id="education" />
        </>

    )
}

export default NavBar;
