import './App.css';
import React, { useRef} from 'react';
import NavBar from './NavBar/navBar'
import About from './About/about'
import Education from './Education/education'
import Experience from './Experience/experience';
import Publication from './Publication/publication'
import {
  BrowserRouter,
  Route,
  Routes,
  HashRouter
} from "react-router-dom";
import {
  NavBarContainer,
  NavigationContainer,
  NavigationButton
} from './NavBar/navBar.styled'

function App() {
  const aboutRef = useRef()
  const exRef = useRef()
  const pubRef = useRef()
  const eduRef = useRef()
  const handleRefObj = 
    { about: aboutRef,
    education: eduRef,
    experience: exRef,
    publication: pubRef,
  }
  const [navLinkId, setNavLinkId] = useState('')
  useEffect(() => {
      const currentRef = handleRefObj[navLinkId];
      console.log(currentRef)
      if(currentRef && currentRef.current){
          currentRef.current.scrollIntoView({behavior: 'smooth'})
      }
  }, [navLinkId])
  return (
    <div className="App">
          <NavBarContainer>
            <NavigationContainer>
                <NavigationButton onClick={() => setNavLinkId('about')}>About</NavigationButton>
                <NavigationButton onClick={() => setNavLinkId('experience')}>Experience</NavigationButton>
            </NavigationContainer>
        </NavBarContainer>
          <About id="about" ref={aboutRef} />
          <Experience id="experience" ref={exRef}/>
          <Publication id="publication" ref={pubRef}/>
          <Education id="education" ref={eduRef}/>

    </div>
  );
}

export default App;
