import React, { useEffect, useState } from 'react';
import { HashLink as Link } from 'react-router-hash-link';
import {
    NavBarContainer,
    NavigationContainer,
    NavigationButton
} from './navBar.styled'


const NavBar = React.forwardRef(( {}, ref) => {
    const [navLinkId, setNavLinkId] = useState('')
    useEffect(() => {
        const currentRef = ref[navLinkId];
        console.log(currentRef, ref)
        if(currentRef && currentRef.current){
            currentRef.current.scrollIntoView({behavior: 'smooth'})
        }
    }, [navLinkId])

    return (
        <NavBarContainer>
            <NavigationContainer>
                <NavigationButton onClick={() => setNavLinkId('about')}>About</NavigationButton>
                <NavigationButton onClick={() => setNavLinkId('experience')}>Experience</NavigationButton>
            </NavigationContainer>
        </NavBarContainer>
    )
})

export default NavBar;
