import React, { useEffect, useState } from 'react';
import { HashLink as Link } from 'react-router-hash-link';
import {
    NavBarContainer,
    NavigationContainer,
    NavigationButton
} from './navBar.styled'


const NavBar = ({ handleRefObj }) => {
    const [navLinkId, setNavLinkId] = useState('')
    useEffect(() => {
        const el = document.getElementById('container');
        console.log(el);
    }, [navLinkId])
    const handleClick = (scrollToId) => {
        console.log(document.getElementById(scrollToId))
		// document.getElementById(scrollToId).scrollIntoView({
		// 	behavior: 'smooth', // gives an ease-in-out effect to our scroll
		// });
	};

    return (
        <NavBarContainer>
            <NavigationContainer>
                <NavigationButton onClick={() => setNavLinkId('about')}>About</NavigationButton>
                <NavigationButton onClick={() => setNavLinkId('experience')}>Experience</NavigationButton>
            </NavigationContainer>
        </NavBarContainer>
    )
}

export default NavBar;
